import { Box } from "@mui/system";
import {
  FormLabel,
  TextField,
  FormControlLabel,
  Checkbox,
} from "@mui/material";
import Button from "@mui/material/Button";
import React, { useState } from "react";

function AddBook() {
  const [inputs, setInputs] = useState({
    name: "",
    author: "",
    desc: "",
    price: 0,
    img: "",
    available: false,
  });

  const [checked, setChecked] = useState(false);

  const handleChange = (e) => {
    setInputs((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  };

  return (
    <form>
      <Box
        display="flex"
        flexDirection="column"
        maxWidth={700}
        marginTop={10}
        marginLeft="auto"
        marginRight="auto"
      >
        <FormLabel>Name</FormLabel>
        <TextField
          value={inputs.name}
          onChange={handleChange}
          fullWidth
          name="name"
          variant="outlined"
        />
        <FormLabel>Author</FormLabel>
        <TextField
          value={inputs.author}
          onChange={handleChange}
          fullWidth
          name="author"
          variant="outlined"
        />
        <FormLabel>Description</FormLabel>
        <TextField
          value={inputs.desc}
          onChange={handleChange}
          fullWidth
          name="desc"
          variant="outlined"
        />
        <FormLabel>Price</FormLabel>
        <TextField
          value={inputs.price}
          onChange={handleChange}
          type="number"
          fullWidth
          name="price"
          variant="outlined"
        />
        <FormLabel>Image</FormLabel>
        <TextField
          value={inputs.img}
          onChange={handleChange}
          fullWidth
          name="img"
          variant="outlined"
        />
        <FormControlLabel
          control={
            <Checkbox checked={checked} onChange={() => setChecked(!checked)} />
          }
          label="Available"
        />
        <Button variant="contained" type="submit">
          Add Book
        </Button>
      </Box>
    </form>
  );
}

export default AddBook;
