import * as React from "react";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogContentText from "@mui/material/DialogContentText";
import DialogTitle from "@mui/material/DialogTitle";
import { Grid } from "@mui/material";
import { useState } from "react";
import axios from "axios";

export default function UserForm({ open, handleClose }) {
  const [values, setValues] = useState([
    {
      email: "",
      firstName: "",
      lastName: "",
      avatar: "",
    },
  ]);

  const closeDialog = () => {
    // handleClose(false);
  };

  const handleChange = (e) => {
    console.log("first", e.target.value, values);
    let data = values[0];
    console.log("data", data, e.target.name);
    data[e.target.name] = e.target.value;
    console.log("data ::", data);
    setValues(data);
  };

  const handleSaveData = () => {
    console.log("values", values);
    if (values[0].firstName === "") {
      alert("Please Enter First Name");
    } else if (values[0].lastName === "") {
      alert("Please Enter Last Name");
    } else if (values[0].email === "") {
      alert("Please Enter Your Email ID");
    } else {
      console.log("else pat");
      let myObj = {
        email: values[0].email,
        first_name: values[0].firstName,
        last_name: values[0].lastName,
        avatar: values[0].avatar,
      };
      axios
        .post("http://localhost:5000/users", myObj)
        .then((res) => {
          console.log("res", res);
          setValues({
            email: "",
            firstName: "",
            lastName: "",
            avatar: "",
          });
          // handleClose(false);
          // setMyArray(res.data.userData);
        })
        .catch((err) => {
          console.log("err", err);
        });
    }
  };

  return (
    <div>
      <Dialog open={open} onClose={closeDialog}>
        <DialogTitle>User Form</DialogTitle>
        <DialogContent>
          <Grid container width={"100%"} alignItems={"center"}>
            <Grid item xs={12} md={12} lg={12}>
              <TextField
                autoFocus
                margin="dense"
                id="firstName"
                label="First Name"
                type="firstName"
                fullWidth
                name="firstName"
                variant="standard"
                value={values.firstName}
                onChange={handleChange}
              />
            </Grid>
          </Grid>
          <Grid container width={"100%"} alignItems={"center"}>
            <Grid item xs={12} md={12} lg={12}>
              <TextField
                autoFocus
                margin="dense"
                id="lastName"
                label="Last Name"
                type="lastName"
                fullWidth
                name="lastName"
                variant="standard"
                value={values.lastName}
                onChange={handleChange}
              />
            </Grid>
          </Grid>
          <Grid container width={"100%"} alignItems={"center"}>
            <Grid item xs={12} md={12} lg={12}>
              <TextField
                autoFocus
                margin="dense"
                id="email"
                label="Email Address"
                type="email"
                fullWidth
                name="email"
                variant="standard"
                value={values.email}
                onChange={handleChange}
              />
            </Grid>
          </Grid>
          <Grid container width={"100%"} alignItems={"center"}>
            <Grid item xs={12} md={12} lg={12}>
              <TextField
                autoFocus
                margin="dense"
                id="avatar"
                label="Profile Url"
                type="avatar"
                fullWidth
                name="avatar"
                variant="standard"
                value={values.avatar}
                onChange={handleChange}
              />
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleSaveData}>Save</Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
